function.body:
/************************************************************************* * The contents of this file are subject to the Compiere Public * License 1.1 ("License"); You may not use this file except in * compliance with the License. You may obtain a copy of the License in * the legal folder of your Openbravo installation. * Software distributed under the License is distributed on an * "AS IS" basis, WITHOUT WARRANTY OF ANY KIND, either express or * implied. See the License for the specific language governing rights * and limitations under the License. * The Original Code is Compiere ERP & Business Solution * The Initial Developer of the Original Code is Jorg Janke and ComPiere, Inc. * Portions created by Jorg Janke are Copyright (C) 1999-2001 Jorg Janke, * parts created by ComPiere are Copyright (C) ComPiere, Inc.; * All Rights Reserved. * Contributor(s): Openbravo SLU * Contributions are Copyright (C) 2001-2008 Openbravo, S.L.U. * * Specifically, this derivative work is based upon the following Compiere * file and version. ************************************************************************* * $Id: AD_Window_Copy.sql,v 1.5 2003/02/07 05:49:59 jjanke Exp $ *** * Title: Copy Window Content (i.e Tabs and Fields) * Description: ************************************************************************/ -- Logistice v_ResultStr VARCHAR2(2000):=''; v_Message VARCHAR2(2000):=''; v_Record_ID VARCHAR2(32); -- Parameter TYPE RECORD IS REF CURSOR; Cur_Parameter RECORD; -- Parameter Variables v_AD_Window_ID VARCHAR2(32); v_NextNo_T VARCHAR2(32); v_NextNo_F VARCHAR2(32); v_NoOfTabs NUMBER:=0; v_NoOfFields NUMBER:=0; Cur_Tabs RECORD; Cur_Fields RECORD; v_AD_Module_ID VARCHAR2(32); ad_language_target VARCHAR(6); BEGIN -- Update AD_PInstance DBMS_OUTPUT.PUT_LINE('Updating PInstance - Processing ' || p_PInstance_ID) ; v_ResultStr:='PInstanceNotFound'; AD_UPDATE_PINSTANCE(p_PInstance_ID, NULL, 'Y', NULL, NULL) ; BEGIN --BODY -- Get Parameters v_ResultStr:='ReadingParameters'; FOR Cur_Parameter IN (SELECT i.Record_ID, p.ParameterName, p.P_String, p.P_Number, p.P_Date FROM AD_PInstance i LEFT JOIN AD_PInstance_Para p ON i.AD_PInstance_ID=p.AD_PInstance_ID WHERE i.AD_PInstance_ID=p_PInstance_ID ORDER BY p.SeqNo ) LOOP v_Record_ID:=Cur_Parameter.Record_ID; IF(Cur_Parameter.ParameterName='AD_Window_ID') THEN v_AD_Window_ID:=Cur_Parameter.P_String; DBMS_OUTPUT.PUT_LINE(' AD_Window_ID=' || v_AD_Window_ID) ; ELSE DBMS_OUTPUT.PUT_LINE('*** Unknown Parameter=' || Cur_Parameter.ParameterName) ; END IF; END LOOP; -- Get Parameter DBMS_OUTPUT.PUT_LINE(' Record_ID=' || v_Record_ID) ; v_ResultStr:='GetEntityType';
--Insert fields in the same module as the window SELECT AD_Module_ID INTO v_AD_Module_ID FROM AD_Window WHERE AD_Window_ID = v_Record_ID;
select AD_language into ad_language_target from ad_module where ad_module_id=v_AD_Module_ID;
-- Record_ID is the Window_ID to copy to FOR Cur_Tabs IN (SELECT * FROM AD_Tab WHERE AD_Window_ID=v_AD_Window_ID) LOOP -- Get next no AD_Sequence_Next('AD_Tab', Cur_Tabs.AD_Client_ID, v_NextNo_T) ; -- Insert INSERT INTO AD_Tab ( AD_TAB_ID, AD_Window_ID, AD_CLIENT_ID, AD_ORG_ID, ISACTIVE, CREATED, CREATEDBY, UPDATED, UPDATEDBY, NAME, DESCRIPTION, HELP, AD_TABLE_ID, TabLevel, SEQNO, ISSINGLEROW, ISINFOTAB, ISTRANSLATIONTAB, ISREADONLY, AD_COLUMN_ID, HASTREE, WHERECLAUSE, ORDERBYCLAUSE, COMMITWARNING, AD_PROCESS_ID, PROCESSING, AD_Module_ID ) select v_NextNo_T, v_Record_ID, Cur_Tabs.AD_CLIENT_ID, Cur_Tabs.AD_ORG_ID, Cur_Tabs.ISACTIVE, now(), Cur_Tabs.CREATEDBY, now(), Cur_Tabs.UPDATEDBY, coalesce(sub.name, Cur_Tabs.NAME), coalesce(sub.description, Cur_Tabs.DESCRIPTION), coalesce(sub.help, Cur_Tabs.HELP), Cur_Tabs.AD_TABLE_ID, Cur_Tabs.TabLevel, Cur_Tabs.SEQNO, Cur_Tabs.ISSINGLEROW, Cur_Tabs.ISINFOTAB, Cur_Tabs.ISTRANSLATIONTAB, Cur_Tabs.ISREADONLY, Cur_Tabs.AD_COLUMN_ID, Cur_Tabs.HASTREE, Cur_Tabs.WHERECLAUSE, Cur_Tabs.ORDERBYCLAUSE, Cur_Tabs.COMMITWARNING, Cur_Tabs.AD_PROCESS_ID, Cur_Tabs.PROCESSING, v_AD_Module_ID FROM ad_tab s left join (select * from AD_tab_trl where ad_language=ad_language_target and ad_tab_id=Cur_Tabs.ad_tab_id ) sub on sub.ad_tab_id=s.ad_tab_id where s.ad_tab_id=Cur_Tabs.ad_tab_id;
-- Translate UPDATE AD_Tab_Trl SET Name= (SELECT coalesce((select name from AD_Tab_Trl s WHERE s.AD_Language=AD_Tab_Trl.AD_Language and s.AD_Tab_ID=Cur_Tabs.AD_Tab_ID), Cur_Tabs.name) FROM ad_tab t WHERE t.AD_Tab_ID=Cur_Tabs.AD_Tab_ID) , Description= (SELECT coalesce((select description from AD_Tab_Trl s WHERE s.AD_Language=AD_Tab_Trl.AD_Language and s.AD_Tab_ID=Cur_Tabs.AD_Tab_ID), Cur_Tabs.description) FROM ad_tab t WHERE t.AD_Tab_ID=Cur_Tabs.AD_Tab_ID) , Help= (SELECT coalesce((select help from AD_Tab_Trl s WHERE s.AD_Language=AD_Tab_Trl.AD_Language and s.AD_Tab_ID=Cur_Tabs.AD_Tab_ID), Cur_Tabs.help) FROM ad_tab t WHERE t.AD_Tab_ID=Cur_Tabs.AD_Tab_ID) WHERE AD_Tab_Trl.AD_Tab_ID=v_NextNo_T; -- Copy Fields FOR Cur_Fields IN (SELECT * FROM AD_Field WHERE AD_Tab_ID=Cur_Tabs.AD_Tab_ID) LOOP -- Get next no AD_Sequence_Next('AD_Field', Cur_Fields.AD_Client_ID, v_NextNo_F) ; -- Insert INSERT INTO AD_Field ( AD_Field_ID, AD_Tab_ID, AD_Client_ID, AD_Org_ID, IsActive, Created, CreatedBy, Updated, UpdatedBy, Name, Description, Help, IsCentrallyMaintained, AD_Column_ID, AD_FieldGroup_ID, ISDISPLAYED, DISPLAYLOGIC, DISPLAYLENGTH, ISREADONLY, SEQNO, SORTNO, ISSAMELINE, ISFIELDONLY, ISENCRYPTED, AD_Module_ID, ShowInRelation, Grid_Seqno ) SELECT v_NextNo_F, v_NextNo_T, Cur_Fields.AD_Client_ID, Cur_Fields.AD_Org_ID, Cur_Fields.IsActive, now(), '0', now(), '0', coalesce(sub.name, Cur_Fields.Name), coalesce(sub.description, Cur_Fields.Description), coalesce(sub.help, Cur_Fields.Help), Cur_Fields.IsCentrallyMaintained, Cur_Fields.AD_Column_ID, Cur_Fields.AD_FieldGroup_ID, Cur_Fields.ISDISPLAYED, Cur_Fields.DISPLAYLOGIC, Cur_Fields.DISPLAYLENGTH, Cur_Fields.ISREADONLY, Cur_Fields.SEQNO, Cur_Fields.SORTNO, Cur_Fields.ISSAMELINE, Cur_Fields.ISFIELDONLY, Cur_Fields.ISENCRYPTED, v_AD_Module_ID, Cur_Fields.ShowInRelation, Cur_Fields.Grid_Seqno FROM ad_field s left join (select * from AD_field_trl where ad_language=ad_language_target and ad_field_id=Cur_Fields.ad_field_id ) sub on sub.ad_field_id=s.ad_field_id where s.ad_field_id=Cur_Fields.ad_field_id;
-- update translation UPDATE AD_Field_Trl SET Name= (SELECT coalesce((select name from AD_field_Trl s WHERE s.AD_Language=AD_field_Trl.AD_Language and s.AD_field_ID=Cur_Fields.AD_field_ID), Cur_Fields.name) FROM ad_field t WHERE t.AD_field_ID=Cur_Fields.AD_field_ID) , Description= (SELECT coalesce((select description from AD_field_Trl s WHERE s.AD_Language=AD_field_Trl.AD_Language and s.AD_field_ID=Cur_Fields.AD_field_ID), Cur_Fields.Description) FROM ad_field t WHERE t.AD_field_ID=Cur_Fields.AD_field_ID) , Help= (SELECT coalesce((select help from AD_field_Trl s WHERE s.AD_Language=AD_field_Trl.AD_Language and s.AD_field_ID=Cur_Fields.AD_field_ID), Cur_Fields.Help) FROM ad_field t WHERE t.AD_field_ID=Cur_Fields.AD_field_ID) WHERE AD_Field_Trl.AD_Field_ID=v_NextNo_F; -- v_NoOfFields:=v_NoOfFields + 1; END LOOP; -- Field v_NoOfTabs:=v_NoOfTabs + 1; END LOOP; -- Tab v_Message:='@Copied@=' || v_NoOfTabs || '/' || v_NoOfFields; --<<FINISH_PROCESS>> -- Update AD_PInstance DBMS_OUTPUT.PUT_LINE('Updating PInstance - Finished ' || v_Message) ; AD_UPDATE_PINSTANCE(p_PInstance_ID, NULL, 'N', 1, v_Message) ; RETURN; END; --BODY EXCEPTION WHEN OTHERS THEN v_ResultStr:= '@ERROR=' || SQLERRM; DBMS_OUTPUT.PUT_LINE(v_ResultStr) ; AD_UPDATE_PINSTANCE(p_PInstance_ID, NULL, 'N', 0, v_ResultStr) ; RETURN; END AD_WINDOW_COPY
