BEGIN
IF AD_isTriggerEnabled()='N' THEN RETURN; END IF;
IF(UPDATING) THEN IF NOT((COALESCE(:old.CLASSNAME, '.') <> COALESCE(:NEW.CLASSNAME, '.') OR COALESCE(:old.IsActive, '.') <> COALESCE(:NEW.IsActive, '.'))) THEN RETURN; END IF; END IF; IF(DELETING OR UPDATING) THEN IF :OLD.ISACTIVE='Y' AND :OLD.CLASSNAME IS NOT NULL THEN DELETE FROM AD_MODEL_OBJECT_MAPPING WHERE AD_MODEL_OBJECT_ID IN ( SELECT AD_MODEL_OBJECT_ID FROM AD_MODEL_OBJECT WHERE AD_MODEL_OBJECT.AD_TASK_ID=:old.AD_TASK_ID AND ACTION='T' ) ; DELETE FROM AD_MODEL_OBJECT WHERE ACTION='T' AND AD_MODEL_OBJECT.AD_TASK_ID=:OLD.AD_TASK_ID; END IF; END IF; IF(INSERTING OR UPDATING) THEN IF :NEW.ISACTIVE='Y' AND :NEW.CLASSNAME IS NOT NULL THEN v_ClassName:=:new.CLASSNAME; Ad_Sequence_Next('AD_Model_Object', :new.AD_Client_ID, v_ID) ; INSERT INTO AD_MODEL_OBJECT ( AD_MODEL_OBJECT_ID, AD_CLIENT_ID, AD_ORG_ID, ISACTIVE, CREATED, CREATEDBY, UPDATED, UPDATEDBY, ACTION, AD_TASK_ID, CLASSNAME, ISDEFAULT ) VALUES ( v_ID, :new.AD_CLIENT_ID, :new.AD_ORG_ID, 'Y', now(), :new.CREATEDBY, now(), :new.UPDATEDBY, 'T', :new.AD_TASK_ID, 'org.openbravo.erpCommon.' || v_WindowName || '.' || v_ClassName, 'Y' ) ; Ad_Sequence_Next('AD_Model_Object_Mapping', :new.AD_Client_ID, v_ID_MAP) ; INSERT INTO AD_MODEL_OBJECT_MAPPING ( AD_MODEL_OBJECT_MAPPING_ID, AD_CLIENT_ID, AD_ORG_ID, ISACTIVE, CREATED, CREATEDBY, UPDATED, UPDATEDBY, AD_MODEL_OBJECT_ID, MAPPINGNAME, ISDEFAULT ) VALUES ( v_ID_MAP, :new.AD_CLIENT_ID, :new.AD_ORG_ID, 'Y', now(), :new.CREATEDBY, now(), :new.UPDATEDBY, v_ID, ('/' || v_WindowName || '/' || v_ClassName || '.html'), 'Y' ) ; END IF; END IF; END AD_TASK_TRG2
