BEGIN
IF AD_isTriggerEnabled()='N' THEN RETURN; END IF;
IF((COALESCE(:old.Name, '.') <> COALESCE(:NEW.Name, '.') OR COALESCE(:old.IsActive, '.') <> COALESCE(:NEW.IsActive, '.') OR COALESCE(:old.AD_Module_ID,'.') <> COALESCE(:new.AD_Module_ID,'.'))) THEN SELECT (CASE WHEN M.AD_MODULE_ID ='0' THEN NULL ELSE JavaPackage END) INTO v_JavaPackage FROM AD_MODULE M WHERE AD_MODULE_ID = :New.AD_Module_ID;
IF v_JavaPackage IS NOT NULL THEN v_ModuleMapping := '/'||v_JavaPackage||'.'; v_JavaPackage := v_JavaPackage||'.'||AD_MAPPING_FORMAT(TO_CHAR(:NEW.NAME)); ELSE v_ModuleMapping := '/'; v_JavaPackage := AD_MAPPING_FORMAT(TO_CHAR(:NEW.NAME)); END IF;
FOR CUR_DATA IN ( SELECT MO.AD_MODEL_OBJECT_ID, AD_MAPPING_FORMAT(TO_CHAR(:NEW.NAME)) AS WINDOWNAME, AD_MAPPING_FORMAT(TO_CHAR(T.NAME)) AS TABNAME, t.AD_Module_ID, t.AD_Tab_ID FROM AD_MODEL_OBJECT MO, AD_TAB T WHERE MO.AD_TAB_ID=T.AD_TAB_ID AND MO.ACTION='W' AND T.AD_WINDOW_ID=:NEW.AD_WINDOW_ID ) LOOP --Add tab's uuid to name for non-core tabs v_TabName := CUR_DATA.TABNAME; IF (CUR_DATA.AD_MODULE_ID!='0') THEN v_TabName := v_TabName||CUR_DATA.AD_Tab_ID; END IF;
UPDATE AD_MODEL_OBJECT SET CLASSNAME=('org.openbravo.erpWindows.' || v_JavaPackage || '.' || v_TabName), ISACTIVE=:NEW.ISACTIVE WHERE AD_MODEL_OBJECT_ID=CUR_DATA.AD_MODEL_OBJECT_ID and classname not like '%ComboReload%'; UPDATE AD_MODEL_OBJECT_MAPPING SET MAPPINGNAME=(v_ModuleMapping || CUR_DATA.WINDOWNAME || '/' || v_TabName || '_Relation.html'), ISACTIVE=:NEW.ISACTIVE WHERE AD_MODEL_OBJECT_ID=CUR_DATA.AD_MODEL_OBJECT_ID AND MAPPINGNAME LIKE '%_Relation.html'; UPDATE AD_MODEL_OBJECT_MAPPING SET MAPPINGNAME=(v_ModuleMapping || CUR_DATA.WINDOWNAME || '/' || v_TabName || '_Edition.html'), ISACTIVE=:NEW.ISACTIVE WHERE AD_MODEL_OBJECT_ID=CUR_DATA.AD_MODEL_OBJECT_ID AND MAPPINGNAME LIKE '%_Edition.html'; UPDATE AD_MODEL_OBJECT_MAPPING SET MAPPINGNAME=(v_ModuleMapping || CUR_DATA.WINDOWNAME || '/' || v_TabName || '_Excel.xls'), ISACTIVE=:NEW.ISACTIVE WHERE AD_MODEL_OBJECT_ID=CUR_DATA.AD_MODEL_OBJECT_ID AND MAPPINGNAME LIKE '%_Excel.xls'; END LOOP; END IF; END AD_WINDOW_TRG2
