BEGIN
IF AD_isTriggerEnabled()='N' THEN RETURN; END IF;
-- Insert AD_Workflow Trigger -- for Translation -- Access IF INSERTING THEN -- Create Translation Row INSERT INTO AD_Workflow_Trl ( AD_Workflow_Trl_ID, AD_Workflow_ID, AD_Language, AD_Client_ID, AD_Org_ID, IsActive, Created, CreatedBy, Updated, UpdatedBy, Name, Description, Help, IsTranslated ) SELECT get_uuid(), :new.AD_Workflow_ID, AD_Language.AD_Language, :new.AD_Client_ID, :new.AD_Org_ID, :new.IsActive, :new.Created, :new.CreatedBy, :new.Updated, :new.UpdatedBy, :new.Name, :new.Description, :new.Help, 'N' FROM AD_Language, AD_Module M WHERE AD_Language.IsActive='Y' AND IsSystemLanguage='Y' AND M.AD_Module_ID = :new.AD_Module_ID AND M.AD_Language != AD_Language.AD_Language; -- Access for all INSERT INTO AD_WorkFlow_Access ( AD_WorkFlow_ID, AD_Role_ID, AD_Client_ID, AD_Org_ID, IsActive, Created, CreatedBy, Updated, UpdatedBy, IsReadWrite, AD_WorkFlow_Access_ID ) SELECT :new.AD_WorkFlow_ID, r.AD_Role_ID, r.AD_CLIENT_ID, r.AD_ORG_ID, 'Y', now(), '0', now(), '0', 'Y', get_uuid() FROM AD_Role r WHERE isManual='N'; END IF; -- Inserting -- AD_Workflow update trigger -- synchronize name,... IF UPDATING THEN IF(COALESCE(:old.Name, '.') <> COALESCE(:NEW.Name, '.') OR COALESCE(:old.Description, '.') <> COALESCE(:NEW.Description, '.') OR COALESCE(:old.Help, '.') <> COALESCE(:NEW.Help, '.')) THEN UPDATE AD_Workflow_Trl SET IsTranslated='N', Updated=now() WHERE AD_Workflow_ID=:new.AD_Workflow_ID; END IF; END IF; -- Updating END AD_WORKFLOW_TRG
