BEGIN
IF AD_isTriggerEnabled()='N' THEN RETURN; END IF;
IF(UPDATING) THEN IF NOT((COALESCE(:old.StartDate, v_DateNull) <> COALESCE(:NEW.StartDate, v_DateNull))) THEN RETURN; END IF; END IF; -- UPDATE C_Period SET EndDate= ADD_MONTHS(TRUNC(StartDate, 'MM'), 1) -1; :new.EndDate:= ADD_MONTHS(TRUNC(:new.StartDate, 'MM'), 1) -1; END C_PERIOD_TRG2
